var DATABASE_NAME="TAPPAUPS10"
var DATABASE_VERSION="1.0"
var DATABASE_DESCRIPTION="BASE TAPPA PROYECTO DE TITULACION"
var DATABASE_SIZE=200000
var WIDTH_IMAGE=150;
var HEIGHT_IMAGE=150;
var FOLDER_IMAGES="/";

    

    
/***INICIALIZACION DE CLASE DE OBJETOS EN BASE DE DATOS***/
    function objectTableDB(name,columns){
        this.name=name;
        this.columns = columns;
        this.columns["id"]="INTEGER PRIMARY KEY AUTOINCREMENT";
    }

    objectTableDB.prototype.createQuery = function() {
        var columnDescriptions="";
        for (var eachColumn in this.columns){
            if (eachColumn!="id"){
                columnDescriptions=columnDescriptions.concat(",",eachColumn," "+this.columns[eachColumn]);
            }
        }
        var CREATE = "CREATE TABLE IF NOT EXISTS "+this.name+" (id INTEGER PRIMARY KEY AUTOINCREMENT"+columnDescriptions+")";
        return CREATE;
    }


/***CLASE DB CONEXION ABSTRACTA A LA BASE DE DATOS***/
    function DB(name,version,description,size){
        this.name=name;
        this.version=version;
        this.description=description;
        this.size=size;
        this.MODELS={};
        this.CREATE_TABLES=[];
        this.db=null;
    }

    DB.prototype.registerObjectDB=function(name,columns){
        var objDB = new objectTableDB(name,columns);
        this.CREATE_TABLES.push(name);
        this.MODELS[name]=objDB
    }

    DB.prototype.initializeDB=function() {
        if(this.db==null){
           this.db = window.openDatabase(DATABASE_NAME, DATABASE_VERSION, DATABASE_DESCRIPTION, DATABASE_SIZE);
           this.createDataBase();
        }
    }

    DB.prototype.createDataBase=function() {
       var ALL_CREATE_TABLES=this.CREATE_TABLES;
       var ALL_OBJECT_TABLES=this.MODELS;
       this.db.transaction(function(tx) {
            for (var index in ALL_CREATE_TABLES) {
                var objDB=(ALL_OBJECT_TABLES[ALL_CREATE_TABLES[index]]);
                tx.executeSql(objDB.createQuery());           
            } 
       });       
    }

    DB.prototype.insert=function(model,values) {
       this.db.transaction(function(tx) {
            var sql="INSERT INTO "+model+"(";
            var arrayValues=[];
            var valuesPos="*";
            var valuesColumns="*";
            for (var each_value in values) {
                arrayValues.push(values[each_value]);  
                valuesPos=valuesPos+",?";
                valuesColumns=valuesColumns+","+each_value;
            } 
            valuesPos=valuesPos.replace("*,","").replace("*","")
            valuesColumns=valuesColumns.replace("*,","").replace("*","")
            sql=sql+valuesColumns+")values("+valuesPos+")";
            tx.executeSql(sql,arrayValues); 
       },this.errorDataBase);       
    }

    DB.prototype.update=function(model,id,values) {
       this.db.transaction(function(tx) {
            var sql="UPDATE "+model+" SET ";
            var arrayValues=[];
            var valuesPos="*";
            for (var each_value in values) {
                arrayValues.push(values[each_value]);  
                valuesPos=valuesPos+","+each_value+"=?";
            } 
            valuesPos=valuesPos.replace("*,","").replace("*","")
            sql=sql+valuesPos+" WHERE ID=?";
            arrayValues.push(id);
            tx.executeSql(sql,arrayValues); 
       },this.errorDataBase);       
    }

DB.prototype.unlink=function(model,id) {
       this.db.transaction(function(tx) {
            var sql="delete from "+model+" where id=?";
            tx.executeSql(sql,[id]); 
       },this.errorDataBase);       
    }

     DB.prototype.executeSql=function(txFX,errorFx) {
        this.db.transaction(txFX,errorFx);
    }

    DB.prototype.errorDataBase=function(err) {
        alert("Error processing SQL("+err.code+"): "+err.message);
    }

/***INICIALIZACION DE DB CONEXION ABSTRACTA A LA BASE DE DATOS***/
    var dbObject=new DB(DATABASE_NAME,DATABASE_VERSION,DATABASE_DESCRIPTION,DATABASE_SIZE);
    dbObject.registerObjectDB("PICTOGRAM_CATEGORY",{"name":"text not null unique",
        "description":"text","folder":"text"});
    dbObject.registerObjectDB("PICTOGRAM",{"name":"text not null",
        "file":" text not null",
        "category_id":"integer default 0 references PICTOGRAM_CATEGORY on delete set default"});
    dbObject.registerObjectDB("PICTOGRAM_PREFERENCES",{"name":"text not null",
        "code":"text"});
    dbObject.registerObjectDB("PICTOGRAM_PREFERENCES_LINE",{"pictogram_id":"integer default 0 references PICTOGRAM on delete set default",
        "preference_id":"integer default 0 references PICTOGRAM_PREFERENCES on delete set default",
        "phrase":"text",
        "sequence":"integer default 0"});
     dbObject.registerObjectDB("SYSTEM_PARAMETERS",{"code_parameter":"text not null",
        "value_parameter":"text not null"});
    dbObject.initializeDB();
    

/***INTEGRACION DE PAGINA CON BASE DE DATOS***/

    function insertDB(model,values) {
        dbObject.insert(model,values);
    }

     function updateDB(model,id,values) {
        dbObject.update(model,id,values);
    }

     function deleteDB(model,id) {
        dbObject.unlink(model,id);
    }

  /***MIGRACION DE BASE DE DATOS***/
    function loadDataBase() {
        dbObject.executeSql(getAllDatas, dbObject.errorDataBase);
    }

    function getAllDatas(tx){
        tx.executeSql('SELECT count(id) as counter from PICTOGRAM_CATEGORY order by name asc', [], loadPictogramCategoryDB);
        tx.executeSql('SELECT count(id) as counter from PICTOGRAM order by name asc', [], loadPictogramDB);
        tx.executeSql("SELECT count(id) as counter from SYSTEM_PARAMETERS where code_parameter='PATH_FILE'", [], loadParametersDB);  
    }
    

    function loadParametersDB(tx, results) {
       if (results.rows.length>0) {
        row=results.rows.item(0);
        if (row["counter"]<=0){
            var datas=[['PATH_FILE','/']];
            for (each_value in datas){
             tx.executeSql("INSERT INTO SYSTEM_PARAMETERS(code_parameter,value_parameter)values(?,?)",datas[each_value]);       
         }
         }
       }
    }

    
    function getParameters() {
        dbObject.executeSql(searchParameters, dbObject.errorDataBase);
    }

    function searchParameters(tx){
        tx.executeSql("SELECT value_parameter from SYSTEM_PARAMETERS where code_parameter='PATH_FILE'", [], function(tx,results){
if (results.rows.length>0) {
        row=results.rows.item(0);
        FOLDER_IMAGES=row["value_parameter"];
       }

        });  
    }


